                        { validator: function (rule, value, callback) {
                            value = value.replace(/(^\s+)|\s+$/g,"");
                            var MobileRegex = /^1[0-9]{10}$/;
                            if (value.length == 0) {
                                callback(new Error('不能为空'))
                            } else {
                                if(value.substr(0, 4) != "0000"){//0000的放过
                                    if (!MobileRegex.test(value)) {
                                        callback(new Error('手机号码格式不正确！'))
                                    } else {
                                        callback();
                                    }
                                } else {
                                    callback();
                                }
